import React, { useState, useEffect } from "react";
import { Button, Col, Container, Row, Card } from "react-bootstrap";

import { listMenuSection } from "../../functions/menusection";

const Cashier = () => {
  //Initial Variable

  const [dataListMenuSection, setDataListMenuSection] = useState([]);
  const [dataListMenuShow, setDataListMenuShow] = useState([]);

  const initialState = [
    { id: 1, name: "Alice", menuSectionName: "กาแฟ" },
    { id: 2, name: "Bob", menuSectionName: "ชา" },
  ];

  const [employees, setEmployees] = useState(initialState);

  console.log(dataListMenuSection);

  useEffect(() => {
    loadDataMenuSection();
  }, []);

  // Fetch Data
  const loadDataMenuSection = () => {
    listMenuSection()
      .then((res) => {
        setDataListMenuSection(res.data);
        setDataListMenuShow(res.data);
      })
      .catch((error) => {
        console.log(error.response.data);
      });
  };

  //Handling Event
  const handleClickSort = (data) => {
    setEmployees((dataCurrent) =>
      dataCurrent.filter((listMenu) => {
        return listMenu.menuSectionName !== data;
      })
    );
  };

  const handleClickShowList = () => {
    console.log(employees);
  };

  return (
    <div>
      <Container fluid>
        <Row>
          <Col md={9} className="mt-2">
            <Row>
              <Col>
                <Button
                  variant="primary"
                  size="lg"
                  onClick={() => handleClickShowList()}
                >
                  แสดง
                </Button>
                {"   "}
                {dataListMenuSection.map((item, index) => (
                  <>
                    <Button
                      variant="primary"
                      size="lg"
                      onClick={() => handleClickSort(item.menuSectionName)}
                    >
                      {item.menuSectionName}
                    </Button>
                    {"   "}
                  </>
                ))}
              </Col>
            </Row>
            <Row>
              <Col>
                <Card style={{ width: "18rem" }}>
                  <Card.Img variant="top" src="holder.js/100px180" />
                  <Card.Body>
                    <Card.Title>Card Title</Card.Title>
                    <Card.Text>
                      Some quick example text to build on the card title and
                      make up the bulk of the card's content.
                    </Card.Text>
                    <Button variant="primary">Go somewhere</Button>
                  </Card.Body>
                </Card>
              </Col>
            </Row>
          </Col>
          <Col md={3}>2</Col>
        </Row>
      </Container>
    </div>
  );
};

export default Cashier;
